import io.kotest.matchers.shouldBe
import kotlin.math.max
import org.junit.Test

/** https://leetcode.com/explore/interview/card/google/59/array-and-strings/3054/ */
class `Longest Substring with At Most Two Distinct Characters` {
  @Test
  fun bigString() {
    lengthOfLongestSubstringTwoDistinct(
            "")
        .shouldBe(17)
  }
  @Test
  fun smallString() {
    lengthOfLongestSubstringTwoDistinct("ccaabbb").shouldBe(5)
  }

  fun lengthOfLongestSubstringTwoDistinct(string: String): Int {
    // This i O(n) because we have to start with each index
    // but for each index we will likely have much less iterations
    // if we found a big substring and we have less chars remaining we can bail
    return (0..string.lastIndex).fold(0) { max, startIndex ->
      when {
        string.length - startIndex < max -> max
        else -> Math.max(countSegmentLength(string, startIndex), max)
      }
    }
  }

  private fun countSegmentLength(string: String, startIndex: Int): Int {
    var length = 0
    val distinctChars = mutableListOf<Char>()
    for (index in startIndex..string.lastIndex) {
      val next = string[index]
      if (next in distinctChars) length++
      else if (distinctChars.size < 2) {
        distinctChars.add(next)
        length++
      } else break
    }
    return length
  }
}
